INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96195, 'Efavirenz', 'Trovafloxacin', 'Moderate', 'Coadministration of efavirenz with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Efavirenz has been associated with hepatotoxicity during postmarketing use.', 'DDInter', 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; other HIV reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of liver function tests should occur before and during treatment, especially in patients with underlying hepatic disease (including hepatitis B or C coinfection) or marked transaminase elevations.', 'Synergism', 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96299/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Enoxacin, Delafloxacin, Nalidixic acid, Cinoxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96196, 'Efavirenz', 'Tucatinib', 'Moderate', 'Coadministration with CYP450 3A4 inducers may decrease the plasma concentrations of tucatinib, which is primarily metabolized by CYP450 2C8 and to a lesser extent by CYP450 3A4.', 'DDInter', 'The potential for diminished pharmacologic effects of tucatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of tucatinib should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96300/', '', 'Lamivudine, Glecaprevir, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Dolutegravir, Oseltamivir, More', 'Tivozanib, Futibatinib, Trametinib, Dacomitinib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96197, 'Efavirenz', 'Ubrogepant', 'Moderate', 'Coadministration with moderate or weak inducers of CYP450 3A4 may decrease the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme. Dedicated drug interaction studies have not been conducted to assess concomitant use of ubrogepant with moderate or weak CYP450 3A4 inducers.', 'DDInter', 'The manufacturer recommends an initial ubrogepant dose of 100 mg when coadministered with moderate or weak CYP450 3A4 inducers. If needed, a second 100 mg dose of ubrogepant may be administered at least 2 hours after the initial dose.', 'Metabolism', 'The manufacturer recommends an initial ubrogepant dose of 100 mg when coadministered with moderate or weak CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96301/', '', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, Acyclovir, More', 'Frovatriptan, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Erenumab, Fremanezumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96198, 'Efavirenz', 'Ulipristal', 'Moderate', 'The concomitant use of CYP450 3A4 inducers may decrease the plasma concentrations and efficacy of ulipristal, which is primarily metabolized by the isoenzyme. Induction of CYP450 3A4 may last for up to 4 weeks after discontinuation of the enzyme inducer.', 'DDInter', 'Concomitant use of ulipristal with CYP450 3A4 inducers should be avoided if possible and nonhormonal emergency contraception should be considered.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96302/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Velpatasvir, More', 'Estetrol, Ospemifene, Raloxifene, Bazedoxifene', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96199, 'Efavirenz', 'Upadacitinib', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of upadacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response.', 'DDInter', 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inducers. Pharmacologic effects of upadacitinib should be monitored more closely whenever a moderate CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96303/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Anakinra, Inebilizumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96200, 'Efavirenz', 'Valbenazine', 'Major', 'Efavirenz can cause significant QT interval prolongation in patients with the CYP450 2B6 *6/*6 genotype due to reduced metabolic clearance of the drug stemming from decreased levels of functional CYP450 2B6 isoenzyme.', 'DDInter', 'Alternatives to efavirenz should be considered in patients receiving other drugs that can prolong the QT interval. Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Alternatives to efavirenz should be considered in patients receiving other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96304/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Velpatasvir, More', 'Dextromethorphan, Dalfampridine, Sodium oxybate, Amifampridine, Tafamidis', 1767369485);
