INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96496, 'Elbasvir', 'Binimetinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp), uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7, and/or breast cancer resistance protein (BCRP) may increase the plasma levels and risk of adverse effects of binimetinib. The proposed mechanism involves the reduced metabolic clearance of binimetinib through inhibition of P-gp, UGT 1A1, UGT 2B7, and/or BCRP.', 'DDInter', 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors. Binimetinib should be monitored more closely whenever a P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitor is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with P-gp, UGT 1A1, UGT 2B7, and/or BCRP inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96600/', '', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Rilpivirine, Etravirine, Dolutegravir, Oseltamivir, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96497, 'Grazoprevir', 'Boceprevir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of grazoprevir, which is a substrate of the isoenzyme. In 8 study subjects, administration of a single 100 mg dose of grazoprevir with the potent CYP450 3A4 inhibitor ketoconazole (400 mg once daily) increased grazoprevir peak plasma concentration (Cmax), systemic exposure (AUC) and trough plasma concentration (Cmin) by 13%, 200% and 100%, respectively, compared to administration of grazoprevir alone. High plasma levels of grazoprevir may increase the risk of adverse effects such as alanine aminotransferase (ALT) elevations. Ketoconazole also increased the Cmax, AUC and Cmin of a single 50 mg dose of elbasvir by 29%, 80% and 89%, respectively.', 'DDInter', 'Concomitant use of elbasvir-grazoprevir with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96601/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, Rilpivirine, More', 'Lamivudine, Glecaprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Telaprevir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96498, 'Grazoprevir', 'Bosentan', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of elbasvir and grazoprevir, both of which are substrates of the isoenzyme.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of elbasvir-grazoprevir with moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96602/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, More', 'Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Oseltamivir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96499, 'Elbasvir', 'Butabarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of elbasvir and grazoprevir, both of which are substrates of the isoenzyme.', 'DDInter', 'The potential for diminished pharmacologic effects of elbasvir-grazoprevir should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of elbasvir-grazoprevir should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96603/', '', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Cobicistat, Acyclovir, More', 1767369485);
