INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96541, 'Grazoprevir', 'Fosamprenavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of grazoprevir, which is a substrate of the isoenzyme. In 8 study subjects, administration of a single 100 mg dose of grazoprevir with the potent CYP450 3A4 inhibitor ketoconazole (400 mg once daily) increased grazoprevir peak plasma concentration (Cmax), systemic exposure (AUC) and trough plasma concentration (Cmin) by 13%, 200% and 100%, respectively, compared to administration of grazoprevir alone. High plasma levels of grazoprevir may increase the risk of adverse effects such as alanine aminotransferase (ALT) elevations. Ketoconazole also increased the Cmax, AUC and Cmin of a single 50 mg dose of elbasvir by 29%, 80% and 89%, respectively.', 'DDInter', 'Concomitant use of elbasvir-grazoprevir with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96645/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, Rilpivirine, More', 'Glecaprevir, Cabotegravir, Amprenavir, Zanamivir, Lenacapavir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, Remdesivir, Famciclovir, More', 1767369485);
