INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96599, 'Elbasvir', 'Rimegepant', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of rimegepant. The proposed mechanism is decreased clearance due to inhibition of P-gp- and/or BCRP-mediated metabolism of rimegepant.', 'DDInter', 'Coadministration of rimegepant with P-gp and/or BCRP inhibitors should be avoided.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96703/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Frovatriptan, Dihydroergotamine, Eptinezumab, Lasmiditan, Atogepant, Eletriptan, Rizatriptan, Ergotamine, Clonidine, Naratriptan, Galcanezumab, More', 1767369485);
