INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96894, 'Ivacaftor', 'Bedaquiline', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', 'DDInter', 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96998/', '', 'Nitric Oxide, Doxapram, Elexacaftor', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96895, 'Ivacaftor', 'Bepridil', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these pathways due to inhibition by ivacaftor and its pharmacologically active M1 metabolite.', 'DDInter', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96999/', '', 'Nitric Oxide, Doxapram, Elexacaftor, Lumacaftor', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96896, 'Ivacaftor', 'Berotralstat', 'Major', 'Coadministration of berotralstat with drugs that are both inhibitors of the efflux transporters P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) as well as substrates of the isoenzyme CYP450 3A4 may lead to an increase in the plasma concentrations and effects of both drugs. Berotralstat is a substrate of both P-gp and BCRP. Increased plasma concentrations of berotralstat may increase the risk of adverse effects, including the potential for QT prolongation. Berotralstat may cause concentration-dependent prolongation of the Fridericia-corrected QT interval (QTcF). In addition, berotralstat is considered a moderate CYP450 3A4 inhibitor and has been reported in drug interaction studies to increase the peak plasma concentration (Cmax) and systemic exposure (AUC) of sensitive CYP450 3A4 substrate midazolam by approximately 1.5-fold and 2.25-fold, respectively.', 'DDInter', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with drugs that are both inhibitors of P-gp or BCRP as well as substrates of CYP450 3A4, such as cyclosporine. Patients should be advised to contact their physician if they experience any undue adverse effects from their medications. Patients should seek prompt medical attention if they experience symptoms that could indicated the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with drugs that are both inhibitors of P-gp or BCRP as well as substrates of CYP450 3A4, such as cyclosporine.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97000/', '', 'Nitric Oxide, Doxapram, Elexacaftor, Lumacaftor', 'Hemin, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96897, 'Ivacaftor', 'Boceprevir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 'For ivacaftor -The ivacaftor dose should be reduced from 1 tablet or 1 packet of oral granules twice a day to 1 tablet or 1 packet of oral granules twice a week (i.e., every 3 to 4 days) during coadministration with potent CYP450 3A4 inhibitors. For example, a patient taking ivacaftor 150 mg tablet twice a day should reduce their ivacaftor dose to a 150 mg tablet twice a week and a patient taking ivacaftor 50 mg oral granule packet twice a day should reduce their ivacaftor dose to 50 mg oral granule packet twice a week, etc.', 'Metabolism', 'For ivacaftor -The ivacaftor dose should be reduced from 1 tablet or 1 packet of oral granules twice a day to 1 tablet or 1 packet of oral granules twice a week (i.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97001/', '', 'Nitric Oxide, Doxapram, Elexacaftor', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96898, 'Elexacaftor', 'Bosentan', 'Moderate', 'Coadministration with elexacaftor may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by elexacaftor and its metabolite. The clinical relevance is unknown.', 'DDInter', 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elexacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97002/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 'Nitric Oxide, Doxapram', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (96899, 'Ivacaftor', 'Butabarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The potential for diminished pharmacologic effects of ivacaftor-containing medications should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ivacaftor-containing medications should be considered during coadministration with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97003/', '', 'Flurazepam, Methohexital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, Paraldehyde, Ramelteon, Ethchlorvynol, More', 'Nitric Oxide, Doxapram, Elexacaftor, Lumacaftor', 1767369485);
