INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (97300, 'Eliglustat', 'Selpercatinib', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers. No dosage adjustment for eliglustat is necessary when used with weak CYP450 3A4 inhibitors in extensive or intermediate metabolizers.', 'Metabolism', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97404/', '', 'Lipoic acid, Triheptanoin, Teduglutide, Fosdenopterin, Triethylenetetramine, Miglustat, Sapropterin, Zinc acetate, Levocarnitine, Laronidase, Imiglucerase, More', 'Acalabrutinib, Tepotinib, Avapritinib, Selumetinib, Vandetanib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, Axitinib, Sunitinib, More', 1767369485);
