INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (97365, 'Felodipine', 'Theophylline', 'Minor', 'In one study, concurrent administration of theophylline and felodipine led to an 18% reduction in theophylline area under the plasma-time concentration curve. Presumably, decreased absorption of theophylline is the cause. If theophylline and felodipine are given together, theophylline levels and patient response should be monitored and the dosage adjusted as needed. Patients should be advised to notify their physician if they experience worsening of their respiratory symptoms.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97469/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Zafirlukast, Bromotheophylline', 'Ivabradine, Nicardipine, Isradipine, Clevidipine, Levamlodipine, Nimodipine, Moexipril, Perindopril, Benazepril, Trandolapril, Fosinopril, Indapamide', 1767369485);
