INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (97398, 'Theophylline', 'Ketamine', 'Moderate', 'Cases of extensor-type seizures have been reported in four asthmatic patients who received aminophylline following induction of anesthesia with ketamine. Causality was not clearly determined due to the presence of other anesthetics. Animal studies suggest that the mechanism of this interaction may involve a lowering of the seizure threshold. Theophylline, dyphylline, and oxtriphylline may also interact.', 'DDInter', 'If aminophylline and ketamine must be used concomitantly, caution should be exercised.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97502/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Mepolizumab, Roflumilast, Pentoxifylline, Zafirlukast, Bromotheophylline', 'Alfentanil, Remifentanil, Sodium oxybate, Isoflurane, Nitrous oxide, Desflurane, Sufentanil, Enflurane, Esketamine, Sevoflurane, Etomidate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (97399, 'Theophylline', 'Ketoconazole', 'Minor', 'Coadministration of ketoconazole with a slow-release preparation of theophylline has been reported to decrease serum theophylline concentration. The mechanism may be related to decreased absorption. Ketoconazole has not been shown to interfere with the pharmacokinetics of intravenous theophylline. Theophylline dosages should be adjusted if necessary. Patients should be monitored for altered efficacy of theophylline if ketoconazole is added to their regimen.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97503/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Pentoxifylline, Zafirlukast, Bromotheophylline', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (97400, 'Theophylline', 'Labetalol', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', 'DDInter', 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', 'Oral and ophthalmic nonselective beta-blockers (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97504/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Mepolizumab, Roflumilast, Zafirlukast, Bromotheophylline', '', 1767369485);
