INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (97503, 'Theophylline', 'Telithromycin', 'Moderate', 'INTERVAL: No clinically relevant pharmacokinetic interaction has been observed with telithromycin and theophylline administered as an extended-release formulation. However, simultaneous administration of theophylline and telithromycin may worsen gastrointestinal side effects such as nausea and vomiting, especially in female patients.', 'DDInter', 'Telithromycin and theophylline should be taken one hour apart to decrease the likelihood of gastrointestinal side effects.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97607/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, Benralizumab, Caffeine, Reslizumab, Dyphylline, Oxtriphylline, Mepolizumab, Roflumilast, More', 'Lincomycin, Clindamycin', 1767369485);
