INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (97801, 'Emapalumab', 'Bacillus calmette-guerin substrain tice live antigen', 'Major', 'The administration of live or live attenuated vaccines during emapalumab therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Emapalumab is a monoclonal antibody that binds to and neutralizes interferon gamma, which may increase susceptibility to certain pathogens and increase the risk of fatal and serious infections.', 'DDInter', 'The use of live or live attenuated vaccines should be avoided during and for at least 4 weeks after the last dose of emapalumab.', 'Others', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97905/', '', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', '', 1767369485);
