INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (97999, 'Empagliflozin', 'Norfloxacin', 'Moderate', 'Quinolone antibiotics may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of quinolones has been associated with disturbances in blood glucose homeostasis possibly stemming from effects on pancreatic beta cell ATP-sensitive potassium channels that regulate insulin secretion. Both hyperglycaemia and hypoglycemia have been reported, usually in diabetic patients receiving concomitant treatment with an oral hypoglycemic agent (e.g., sulfonylurea) or insulin. Although hyperglycaemia is significantly more common and infection itself may be an underlying risk factor, hypoglycemia may cause greater morbidity and mortality.', 'DDInter', 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill. Patients should be apprised of the increased risk of dysglycemia and be particularly alert to potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypoglycemia occurs, patients should initiate appropriate remedial therapy immediately, discontinue the quinolone, and contact their physician. Alternative antibiotics may need to be considered.', 'Antagonism', 'Blood glucose should be closely monitored whenever quinolones are prescribed to diabetic patients, especially if they are elderly, have renal impairment, or are severely ill.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98103/', '', 'Tinidazole, Tetracycline, Cefepime, Fluconazole, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Rifamycin, More', 'Guar gum', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (98000, 'Empagliflozin', 'Octreotide', 'Moderate', 'Somatostatin analogs may alter the therapeutic response to insulin and other antidiabetic agents. Somatostatin analogs can induce hyperglycemia and, less frequently, hypoglycemia, by inhibiting the secretion of various counter-regulatory hormones involved in glucose homeostasis (i.e., glucagon, insulin, growth hormone, incretin hormones).', 'DDInter', 'Close monitoring of diabetic control is recommended if somatostatin analogs are prescribed to patients with preexisting diabetes. Glycemic status including fasting plasma glucose and/or hemoglobin A1c should be assessed prior to initiation of therapy and periodically during therapy in accordance with manufacturer''s product labeling, and the antidiabetic treatment adjusted as necessary.', 'Antagonism', 'Close monitoring of diabetic control is recommended if somatostatin analogs are prescribed to patients with preexisting diabetes.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98104/', '', 'Nafarelin, Cetrorelix, Ganirelix, Relugolix, Gonadorelin', 'Guar gum', 1767369485);
