INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (98296, 'Gatifloxacin', 'Rilpivirine', 'Major', 'Certain quinolones, including gatifloxacin and moxifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98400/', '', 'Rifamycin, Kanamycin, Ampicillin, Acyclovir, Netilmicin, Polymyxin B, Famciclovir, Cefuroxime, Trifluridine, Tobramycin, Benzylpenicillin, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (98297, 'Gemifloxacin', 'Rilpivirine', 'Moderate', 'Certain quinolones, including gemifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation may increase with increasing plasma concentrations of gemifloxacin, the recommended dosage should not be exceeded, especially in patients with renal or hepatic impairment.', 'Synergism', 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98401/', '', 'Enoxacin, Nalidixic acid, Cinoxacin, Trovafloxacin, Delafloxacin', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (98298, 'Gilteritinib', 'Rilpivirine', 'Moderate', 'Gilteritinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with gilteritinib, on days 8 and 15 of cycle 1, and prior to the start of the next two subsequent cycles. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98402/', '', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (98299, 'Glycerin', 'Rilpivirine', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98403/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Alvimopan, Sorbitol, Methylnaltrexone, Tegaserod, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Mannitol, Naldemedine, Prucalopride, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (98300, 'Glycerol phenylbutyrate', 'Rilpivirine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, caution and clinical monitoring may be advisable if CYP450 3A4 inducers are coadministered with rilpivirine.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/98404/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Telaprevir, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 1767369485);
