INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (99258, 'Oxycodone', 'Oxcarbazepine', 'Major', 'Coadministration with enzyme-inducing anticonvulsants may decrease the plasma concentrations of opioids that are primarily metabolized by CYP450 3A4 such as butorphanol, fentanyl, hydrocodone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of an enzyme-inducing anticonvulsant. Conversely, discontinuation of the anticonvulsant may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'Pharmacologic response to the opioid should be monitored more closely whenever an enzyme-inducing anticonvulsant is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary. Due to the potential for additive central nervous system depressant effects, ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them.', 'Metabolism', 'Pharmacologic response to the opioid should be monitored more closely whenever an enzyme-inducing anticonvulsant is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99363/', '', 'Naloxone', 'Lacosamide', 1767369485);
