INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (99990, 'Imipramine', 'Levothyroxine', 'Moderate', 'Coadministration of thyroid hormone replacement therapy with tricyclic antidepressants may accelerate the onset or potentiate the action of tricyclic antidepressants, increasing the risk of cardiac arrhythmias and CNS stimulation. The proposed mechanism may be an increased receptor sensitivity to catecholamines. Some clinicians have used this interaction therapeutically.', 'DDInter', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation. Advise patients to contact their doctor if they experience toxicity symptoms such as: anxiety, agitation, insomnia, shortness of breath, irregular or fast heartbeat, and lightheadedness or dizziness.', 'Synergism', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100104/', '', 'Maprotiline, St. John''s Wort, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Vortioxetine, Tryptophan, Levomilnacipran, Vilazodone, Esketamine, Escitalopram', '', 1767369485);
