INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100139, 'Iron protein succinylate', 'Thyroid, porcine', 'Moderate', 'INTERVAL: Concurrent administration of iron-containing products may decrease the oral bioavailability and pharmacologic effects of levothyroxine. The exact mechanism of interaction is unknown, but in vitro data suggest it may involve nonspecific adsorption of levothyroxine to iron at acidic pH levels, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 'Some experts recommend separating the times of administration of levothyroxine and iron-containing preparations by at least 2 to 4 hours. Monitoring of serum TSH levels is recommended. Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', 'Some experts recommend separating the times of administration of levothyroxine and iron-containing preparations by at least 2 to 4 hours.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100253/', '', 'Cyanocobalamin, Folic acid', '', 1767369485);
