INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100330, 'Aluminum hydroxide', 'Liothyronine', 'Moderate', 'INTERVAL: Concurrent administration of simethicone and/or antacid preparations may decrease the oral bioavailability of levothyroxine. Pharmacologic effects of levothyroxine may be reduced. The exact mechanism of interaction is unknown but may involve nonspecific adsorption of levothyroxine to polyvalent cations, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract. Additionally, because gastric acidity is required for adequate levothyroxine absorption, agents that effect intragastric pH may reduce levothyroxine absorption.', 'DDInter', 'It is recommended to separate the times of administration of levothyroxine and simethicone and antacids or other antacid-containing preparations (e.g., didanosine buffered tablets or pediatric oral solution) by at least 4 hours. Monitoring of serum TSH levels is recommended. Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', 'It is recommended to separate the times of administration of levothyroxine and simethicone and antacids or other antacid-containing preparations (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100444/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100331, 'Aminophylline', 'Liothyronine', 'Moderate', 'The metabolism of theophylline is directly related to thyroid function. Theophylline clearance is decreased in hypothyroid patients and increased in hyperthyroid patients. Toxicity or subtherapeutic levels may occur. Similar interactions may occur with other methylxanthines.', 'DDInter', 'When thyroid hormone dosage is instituted, discontinued, or changed, close observation for altered theophylline effect and serum concentrations is recommended. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms, or any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeats.', 'Others', 'When thyroid hormone dosage is instituted, discontinued, or changed, close observation for altered theophylline effect and serum concentrations is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100445/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Pentoxifylline, Zafirlukast, Bromotheophylline', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100332, 'Amoxapine', 'Liothyronine', 'Moderate', 'Coadministration of thyroid hormone replacement therapy with tricyclic antidepressants may accelerate the onset or potentiate the action of tricyclic antidepressants, increasing the risk of cardiac arrhythmias and CNS stimulation. The proposed mechanism may be an increased receptor sensitivity to catecholamines. Some clinicians have used this interaction therapeutically.', 'DDInter', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation. Advise patients to contact their doctor if they experience toxicity symptoms such as: anxiety, agitation, insomnia, shortness of breath, irregular or fast heartbeat, and lightheadedness or dizziness.', 'Synergism', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100446/', '', 'Phenelzine, Desvenlafaxine, Maprotiline, St. John''s Wort, Nefazodone, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Vortioxetine, Tryptophan, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100333, 'Anisindione', 'Liothyronine', 'Moderate', 'Since thyroid hormones may increase the catabolism of vitamin K-dependent clotting factors, the hypoprothrombinemic response to oral anticoagulants may be enhanced during the initiation of thyroid hormone therapy.', 'DDInter', 'INR or prothrombin time should be monitored closely when initiating, discontinuing, or changing the dosage of thyroid hormone therapy in patients stabilized on their oral anticoagulant regimen. Patients should be advised to notify their physician if they develop signs and symptoms of excessive anticoagulation, such as unusual or prolonged bleeding, bruising, vomiting, change in stool or urine color, headache, dizziness, or weakness.', 'Others', 'INR or prothrombin time should be monitored closely when initiating, discontinuing, or changing the dosage of thyroid hormone therapy in patients stabilized on their oral anticoagulant regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100447/', '', '', '', 1767369485);
