INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100545, 'Protriptyline', 'Liotrix', 'Moderate', 'Coadministration of thyroid hormone replacement therapy with tricyclic antidepressants may accelerate the onset or potentiate the action of tricyclic antidepressants, increasing the risk of cardiac arrhythmias and CNS stimulation. The proposed mechanism may be an increased receptor sensitivity to catecholamines. Some clinicians have used this interaction therapeutically.', 'DDInter', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation. Advise patients to contact their doctor if they experience toxicity symptoms such as: anxiety, agitation, insomnia, shortness of breath, irregular or fast heartbeat, and lightheadedness or dizziness.', 'Synergism', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100659/', '', 'Citalopram, Sertraline, Maprotiline, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, Esketamine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100546, 'Quinestrol', 'Liotrix', 'Moderate', 'Estrogens may increase serum thyrotropin concentration, which could be harmful in patients with thyroid cancer receiving thyroxine for thyrotropin suppression or increase dosage requirements in patients with hypothyroidism receiving thyroxine for replacement therapy. Estrogens are known to increase serum thyroid-binding globulin concentration in a dose-dependent manner. Consequently, there may be a reduction in unbound, or free, thyroxine available for hormone activity, which, in turn, leads to an increase in serum thyrotropin concentration. Normally, thyroxine secretion can increase to compensate for this effect, but patients with hypothyroidism lack the mechanism to adapt.', 'DDInter', 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly. Patients should be advised to contact their physician if clinical manifestations of hypothyroidism occur, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Distribution', 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100660/', '', '', '', 1767369485);
