INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100603, 'Amoxapine', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 2D6 activity by ritonavir.', 'DDInter', 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ritonavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if ritonavir must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100717/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Phenelzine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Tryptophan, Esketamine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100604, 'Amprenavir', 'Lopinavir', 'Major', 'Coadministration with amprenavir may decrease the plasma concentrations of lopinavir, even in the presence of low-dose ritonavir as a pharmacokinetic booster. The proposed mechanism is amprenavir induction of lopinavir metabolism via CYP450 3A4.', 'DDInter', 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with amprenavir. For adults, the dosage should be increased to 500 mg-125 mg (two 200 mg-50 mg tablets and one 100 mg-25 mg tablet) twice daily for the tablets and 533 mg-133 mg (6.5 mL) twice daily for the oral solution. Lopinavir-ritonavir should not be administered as a once daily regimen in combination with amprenavir. For pediatric patients 6 months to 18 years of age, the dosage should be increased to 300 mg-75 mg/m2 twice daily for the oral solution in both treatment-naive and treatment-experienced patients, not to exceed the recommended adult dosage of 533 mg-133 mg (6.5 mL) twice daily. If weight-based dosing is preferred, the recommended dosage is 13 mg-3.25 mg/kg given twice daily for patients under 15 kg and 11 mg-2.75 mg/kg given twice daily for patients 15 kg to 45 kg. Pediatric patients weighing more than 45 kg should receive the adult dosage of lopinavir-ritonavir. Please consult the manufacturer''s product labeling for pediatric dosing of the tablet formulation. Lopinavir-ritonavir should not be administered in combination with amprenavir in patients under 6 months of age due to the lack of clinical data.', 'Metabolism', 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with amprenavir.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100718/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, More', 'Glecaprevir, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, Remdesivir, More', 1767369485);
