INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100616, 'Bedaquiline', 'Lopinavir', 'Major', 'Bedaquiline can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration.', 'Synergism', 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100730/', '', 'Paritaprevir, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100617, 'Benznidazole', 'Ritonavir', 'Moderate', 'Ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, and tipranavir capsules all contain alcohol, which may produce a disulfiram-like reaction when coadministered with drugs that can inhibit aldehyde dehydrogenase (ALDH) such as nitroimidazoles (e.g., metronidazole, tinidazole), nitrofurans (e.g., furazolidone, nifurtimox), and cephalosporins with an N-methylthiotetrazole (NMTT) side chain that structurally resembles disulfiram. Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death. The interaction is well established for disulfiram.', 'DDInter', 'Until further information is available, use of ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, or tipranavir capsules with nitroimidazoles (including vaginal formulations), nitrofurans, and certain cephalosporins should be avoided if possible.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100731/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, More', 'Pentamidine, Miltefosine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100618, 'Bepridil', 'Ritonavir', 'Major', 'Coadministration with ritonavir may significantly increase the plasma concentrations of some antiarrhythmic agents such as amiodarone, bepridil, flecainide, propafenone, and quinidine. The proposed mechanism is ritonavir inhibition of CYP450 3A4 and/or 2D6, the isoenzymes responsible for the metabolic clearance of these agents. Although clinical data are lacking, the interaction could conceivably lead to serious and/or life-threatening reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', 'DDInter', 'The use of ritonavir with amiodarone, bepridil, flecainide, propafenone, or quinidine is considered contraindicated.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100732/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', '', 1767369485);
