INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100754, 'Indacaterol', 'Lopinavir', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100868/', '', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Etravirine, Dolutegravir, Oseltamivir, More', 'Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Tiotropium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100755, 'Indinavir', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may significantly increase the plasma concentrations of indinavir. The mechanism is ritonavir inhibition of indinavir metabolism via CYP450 3A4.', 'DDInter', 'Based on the magnitude of interaction, indinavir dosage should be reduced when coadministered with ritonavir. A regimen of (indinavir:ritonavir) 400:400 mg twice a day with food has been well tolerated in clinical trials and is recommended by some HIV experts. This dosing reportedly produces comparable indinavir AUC as standard indinavir (i.e., unboosted indinavir 800 mg three times a day) but increases plasma trough levels by 4-fold and reduces Cmax by over 50%, which may enhance dosing convenience as well as the safety-to-efficacy profile of indinavir. Lower doses of ritonavir (100 to 200 mg) plus 800 mg of indinavir twice daily with food are also recommended by HIV experts. These regimens appear to be equally effective as standard indinavir but may be somewhat less well tolerated, as indicated by an increased rate of treatment discontinuation and adverse effects such as nephrolithiasis, taste disturbances, and gastrointestinal intolerance in some studies.', 'Metabolism', 'Based on the magnitude of interaction, indinavir dosage should be reduced when coadministered with ritonavir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/100869/', '', 'Paritaprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Oseltamivir, Darunavir, Maribavir, Rimantadine, Elvitegravir, Telbivudine, More', 'Glecaprevir, Cabotegravir, Zanamivir, Lenacapavir, Valaciclovir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, Remdesivir, Famciclovir, More', 1767369