INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100936, 'Pirbuterol', 'Lopinavir', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101050/', '', 'Ephedrine, Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100937, 'Ritonavir', 'Piroxicam', 'Minor', 'Ritonavir has a moderate affinity for the CYP450 2C9 microsomal enzyme and may variably affect drugs that are substrates of this enzyme. The area under the plasma concentration-time curves for these drugs may increase or decrease. If concomitant therapy is necessary, close monitoring for therapeutic and adverse effects is strongly recommended.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101051/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 'Fenoprofen, Meclofenamic acid, Ketorolac, Misoprostol, Ketoprofen, Glucosamine, Oxaprozin, Chondroitin sulfate, Esomeprazole, Tolmetin, Sulindac, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (100938, 'Pitavastatin', 'Lopinavir', 'Major', 'Coadministration with lopinavir-ritonavir may significantly increase the plasma concentrations of pitavastatin. The mechanism of interaction has not been described. The proposed interaction is based on data with another HMG-CoA reductase inhibitor that has a similar pharmacokinetic profile to that of pitavastatin. Although not reported in the study, high levels of HMG-CoA reductase inhibitory activity in plasma is also associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', 'DDInter', 'The use of pitavastatin in combination with lopinavir-ritonavir is not recommended. An alternative such as fluvastatin or pravastatin may be considered.', 'Synergism', 'The use of pitavastatin in combination with lopinavir-ritonavir is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101052/', '', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Cholestyramine, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Cerivastatin, Ezetimibe, Niacin, Atorvastatin, Colesevelam, More', 1767369485);
