INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101100, 'Vismodegib', 'Ritonavir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein may increase the plasma concentrations of vismodegib, which has been shown in vitro to be a substrate of the efflux transporter. The potential for increased pharmacologic effects of vismodegib, including adverse effects such as muscle spasm, alopecia, dysgeusia/ageusia, weight loss, fatigue, nausea, vomiting, diarrhea, decreased appetite, constipation and arthralgia, should be considered.', 'DDInter', 'Caution is advised if vismodegib is used in combination with P-glycoprotein inhibitors.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101214/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Porfimer sodium, Selinexor, More', 1767369485);
