INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101101, 'Vorapaxar', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of vorapaxar, which is a substrate of the isoenzyme.', 'DDInter', 'Concomitant use of vorapaxar with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of vorapaxar during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of vorapaxar with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101215/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Rilpivirine, More', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101102, 'Voriconazole', 'Ritonavir', 'Major', 'Coadministration with ritonavir may significantly decrease or increase the plasma concentrations of voriconazole. The proposed mechanism may involve ritonavir-mediated induction of voriconazole metabolism via the CYP450 isoenzymes 2C19 and 2C9. Voriconazole is primarily metabolized via CYP450 2C19, and to a lesser extent, by CYP450 2C9 and 3A4.', 'DDInter', 'The use of voriconazole in combination with high-dose ritonavir (400 mg or more every 12 hours) is considered contraindicated. Coadministration of voriconazole and low-dose ritonavir (100 mg every 12 hours) should also be avoided, unless the benefit justifies the risk.', 'Metabolism', 'The use of voriconazole in combination with high-dose ritonavir (400 mg or more every 12 hours) is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101216/', '', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, Famciclovir, Darunavir, More', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101103, 'Vortioxetine', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 2D6 may significantly increase the plasma concentrations of vortioxetine, which is primarily metabolized by the isoenzyme. High plasma levels of vortioxetine may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 'The dosage of vortioxetine should be reduced by one-half when used in combination with potent CYP450 2D6 inhibitors such as bupropion, cinacalcet, fluoxetine, paroxetine, quinidine, ritonavir, and terbinafine. Following discontinuation of the potent CYP450 2D6 inhibitor, vortioxetine dosage should be returned to the original level. Since fluoxetine and paroxetine are also selective serotonin reuptake inhibitors, additive serotonergic effects with vortioxetine should be considered, and concomitant use avoided if possible.', 'Metabolism', 'The dosage of vortioxetine should be reduced by one-half when used in combination with potent CYP450 2D6 inhibitors such as bupropion, cinacalcet, fluoxetine, paroxetine, quinidine, ritonavir, and terbinafine.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101217/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Phenelzine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Tryptophan, Esketamine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101104, 'Voxelotor', 'Ritonavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the systemic exposure (AUC) to voxelotor, which has been shown to be a substrate of this isoenzyme.', 'DDInter', 'The use of voxelotor with potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration is required, the manufacturer recommends reducing the voxelotor dose to 1000 mg once daily. Patients should be closely monitored for signs of voxelotor-related adverse effects, including headache, diarrhea, abdominal pain, nausea, and vomiting.', 'Metabolism', 'The use of voxelotor with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101218/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Hemin, Conestat alfa, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101105, 'Warfarin', 'Ritonavir', 'Moderate', 'Coadministration with protease inhibitors may alter the plasma concentrations and/or pharmacologic effects of oral anticoagulants. The exact mechanism of interaction is unknown.', 'DDInter', 'Caution is advised if oral anticoagulants must be used with protease inhibitors. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of the protease inhibitor(s) in patients who are stabilized on their anticoagulant regimen.', 'Others', 'Caution is advised if oral anticoagulants must be used with protease inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101219/', '', 'Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Doravirine, Bictegravir, More', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 1767369485);
