INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101110, 'Ziprasidone', 'Lopinavir', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Coadministration of ziprasidone with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101224/', '', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101111, 'Zolpidem', 'Ritonavir', 'Minor', 'Use of drugs that inhibit the isoenzyme CYP450 3A4 may decrease the metabolism and increase the plasma levels of zolpidem. This can result in increased clinical effectiveness and risk of toxicity associated with zolpidem. Monitoring for signs and symptoms of altered zolpidem effect is recommended. Patients should be advised to notify their doctor if they experience nausea, vomiting, diarrhea, confusion, daytime sedation, dizziness, or unconsciousness. A reduction in zolpidem dosage may be necessary.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101225/', '', 'Remimazolam, Chloral hydrate, Thiopental, Paraldehyde, Meprobamate, Scopolamine, Dexmedetomidine, Methohexital, Ethchlorvynol, Dichloralphenazone, Valerian', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Brincidofovir, More', 1767369485);
