INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101153, 'Butalbital', 'Losartan', 'Minor', 'Concomitant administration of phenobarbital and losartan has been reported to decrease the area under the plasma concentration-time curve (AUC) of losartan and its active metabolite by as much as 20%. The mechanism is thought to be induction of losartan metabolism by phenobarbital. While data is available for phenobarbital only, theoretically this interaction may occur with other barbiturates. The reduction in the AUC was not considered significant, however, clinical monitoring for evidence of altered losartan effect is recommended if these drugs are used together.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101267/', '', 'Suvorexant, Thiopental, Eszopiclone, Ramelteon, Scopolamine, Methohexital, Lemborexant, Estazolam, Ethchlorvynol, Tasimelteon, Melatonin, More', 'Irbesartan, Candesartan, Eprosartan, Olmesartan, Irbesartan, Hydrochlorothiazide, Olmesartan, Candesartan, Eprosartan, Sacubitril, Aliskiren', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101154, 'Choline salicylate', 'Losartan', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', 'DDInter', 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101268/', '', 'Hydrochlorothiazide, Sacubitril', 'Ziconotide, Methoxyflurane', 1767369485);
