INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101156, 'Enzalutamide', 'Losartan', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', 'DDInter', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101270/', '', 'Valsartan, Olmesartan, Azilsartan medoxomil, Telmisartan, Candesartan, Eprosartan, Valsartan, Hydrochlorothiazide, Olmesartan, Azilsartan medoxomil, Nebivolol, More', 'Darolutamide, Toremifene, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Aminoglutethimide, Abiraterone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101157, 'Eplerenone', 'Losartan', 'Moderate', 'Coadministration of eplerenone with ACE inhibitors or angiotensin II receptor antagonists may increase the risk of hyperkalemia due to additive pharmacodynamic effects.', 'DDInter', 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, caution is advised if eplerenone is administered with ACE inhibitors or angiotensin II receptor antagonists. Periodic monitoring of serum potassium levels is recommended until the effect of eplerenone is established. In clinical trials, monitoring occurred every 2 weeks for the first 1 to 2 months, then monthly thereafter. Particular caution is warranted in patients with renal impairment, diabetes, old age, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, caution is advised if eplerenone is administered with ACE inhibitors or angiotensin II receptor antagonists.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101271/', '', 'Sacubitril, Nebivolol', '', 1767369485);
