INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101192, 'Pentobarbital', 'Losartan', 'Minor', 'Concomitant administration of phenobarbital and losartan has been reported to decrease the area under the plasma concentration-time curve (AUC) of losartan and its active metabolite by as much as 20%. The mechanism is thought to be induction of losartan metabolism by phenobarbital. While data is available for phenobarbital only, theoretically this interaction may occur with other barbiturates. The reduction in the AUC was not considered significant, however, clinical monitoring for evidence of altered losartan effect is recommended if these drugs are used together.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101306/', '', 'Suvorexant, Thiopental, Eszopiclone, Ramelteon, Scopolamine, Methohexital, Lemborexant, Estazolam, Ethchlorvynol, Tasimelteon, Melatonin, More', 'Irbesartan, Candesartan, Eprosartan, Olmesartan, Irbesartan, Hydrochlorothiazide, Olmesartan, Candesartan, Eprosartan, Sacubitril, Aliskiren', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101193, 'Perindopril', 'Losartan', 'Major', 'Coadministration of an ACE inhibitor in combination with an angiotensin II receptor antagonist may increase the risk of hyperkalemia, hypotension, syncope, and renal dysfunction due to additive or synergistic effects on the renin-angiotensin system.', 'DDInter', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy. Routine monitoring of electrolytes and renal function may be indicated in the elderly or patients with worsening heart failure or a risk for dehydration. Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Dual blockade of the renin-angiotensin-aldosterone system by adding an ACE inhibitor to an angiotensin II receptor antagonist is not recommended, especially in patients with diabetic nephropathy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101307/', '', 'Nebivolol', 'Bempedoic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101194, 'Phenytoin', 'Losartan', 'Moderate', 'Coadministration with phenytoin may decrease the plasma concentrations of the active metabolite of losartan, known as E3174. The investigators propose that phenytoin may affect non-2C9 pathways for losartan in individuals with wild-type CYP450 2C9 but may induce clearance of losartan and E3174 in individuals with the variant allele. Losartan did not affect the pharmacokinetics of phenytoin.', 'DDInter', 'Because E3174 is responsible for most of the therapeutic effects of losartan, significant reduction in its concentration in the circulation could affect overall clinical efficacy of the drug, although that has not been tested. Until further data are available, pharmacologic response to losartan should be monitored more closely following initiation, discontinuation, or change of dosage of phenytoin.', 'Metabolism', 'Because E3174 is responsible for most of the therapeutic effects of losartan, significant reduction in its concentration in the circulation could affect overall clinical efficacy of the drug, although that has not been tested.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101308/', '', 'Brivaracetam, Ezogabine, Cenobamate, Ethosuximide, Lacosamide, Tiagabine, Ethotoin, Rufinamide, Paramethadione, Perampanel, Ganaxolone, More', 'Azilsartan medoxomil, Candesartan, Eprosartan, Olmesartan, Olmesartan, Azilsartan medoxomil, Nebivolol, Candesartan, Sacubitril, Aliskiren, Eprosartan', 1767369485);
