INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (101600, 'Telmisartan', 'Pregabalin', 'Moderate', 'Pregabalin has been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema (e.g., ACE inhibitors, angiotensin II receptor antagonists, renin inhibitors) may theoretically increase the risk. Specific symptoms included swelling of the face, mouth (tongue, lips, and gums), and neck (throat and larynx). In some cases, the angioedema led to life-threatening respiratory compromise that required emergency treatment.', 'DDInter', 'Clinicians and patients should be aware of the potential for increased risk of angioedema when pregabalin is prescribed with other drugs that are also known to cause angioedema. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Synergism', 'Clinicians and patients should be aware of the potential for increased risk of angioedema when pregabalin is prescribed with other drugs that are also known to cause angioedema.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/101714/', '', 'Brivaracetam, Ezogabine, Cenobamate, Ethosuximide, Lacosamide, Tiagabine, Ethotoin, Rufinamide, Paramethadione, Perampanel, Ganaxolone, More', 'Sacubitril, Nebivolol', 1767369485);
