INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102256, 'Timolol', 'Methyldopa', 'Moderate', 'Methyldopa and beta-blockers may have additive hypotensive effects. In addition, potentiation of hypertensive rebound associated with withdrawal of methyldopa or beta-blockers may occur if both drugs are withdrawn at the same time. The proposed mechanism may involve increased catecholamine release after methyldopa and/or a beta-blocker are withdrawn, which may lead to unopposed alpha-adrenergic effects and vasoconstriction.', 'DDInter', 'Close monitoring of blood pressure is recommended during concomitant use, and if methyldopa or the beta blocker are withdrawn from therapy. Methyldopa manufacturers recommend adjusting the beta blocker dose if methyldopa is added to therapy, and not exceeding a methyldopa dose of 500 mg/day when it is first added to therapy. Patients should be instructed to notify their doctor if they have a reduced heart rate, dizziness, fainting or headaches, chest pain or vision problems.', 'Synergism', 'Close monitoring of blood pressure is recommended during concomitant use, and if methyldopa or the beta blocker are withdrawn from therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102375/', '', 'Esmolol, Betaxolol, Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Betaxolol, Diclofenamide, Bimatoprost, Travoprost, More', 'Deserpidine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102257, 'Tofacitinib', 'Methyldopa', 'Moderate', 'Tofacitinib has been shown to decrease heart rate and prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta-blockers, digoxin, alpha-2 adrenoceptor agonists) may result in elevated risk of conduction disturbances and atrioventricular block.', 'DDInter', 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible. Caution is advised if concomitant use is necessary, particularly in patients with known conduction problems or severe cardiac disease. An ECG should be considered in these patients before initiating concomitant therapy and after titration to steady-state. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeats.', 'Synergism', 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102376/', '', 'Rauwolfia serpentina root, Reserpine, Deserpidine', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
