INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102452, 'Plazomicin', 'Phenyl salicylate', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', 'DDInter', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102571/', '', 'Darifenacin, Vardenafil, Tadalafil, Ammonium chloride, Apomorphine, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, More', 'Neomycin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102453, 'Polyethylene glycol (3350 with electrolytes)', 'Phenyl salicylate', 'Moderate', 'Coadministration with agents that affect renal function or perfusion such as diuretics, ACE inhibitors, angiotensin receptor blockers, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk and/or severity of fluid and electrolyte disturbances associated with the use of bowel cleansing preparations. Fluid and electrolyte disturbances can lead to serious adverse events including cardiac arrhythmias, seizures, and renal impairment.', 'DDInter', 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment. Baseline and postprocedure labs including serum electrolytes, BUN, and creatinine should be considered. Patients should be advised to drink sufficient quantities of clear fluids before, during, and after bowel cleansing. Hospitalization and intravenous fluid hydration may be appropriate for frail or elderly patients who may be unable to drink an adequate volume of fluid.', 'Synergism', 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102572/', '', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Sildenafil, Mirabegron, Alprostadil, Acetohydroxamic acid, Yohimbine, Avanafil, Papaverine, More', '', 1767369485);
