INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102570, 'Metronidazole (topical)', 'Lithium carbonate', 'Moderate', 'Limited data suggest that coadministration with metronidazole may increase the risk of lithium toxicity. The mechanism of interaction has not been described, and it is not known whether the interaction occurs with other nitroimidazoles or vaginally administered metronidazole.', 'DDInter', 'Pharmacologic response and serum lithium levels should be monitored more closely whenever metronidazole is added to or withdrawn from therapy, and the lithium dosage adjusted as necessary. Patients should be advised to notify their physician if they experience early symptoms of lithium toxicity such as diarrhea, vomiting, drowsiness, tremor, lack of coordination, ataxia, or muscular weakness. The same precaution may be applicable during therapy with tinidazole or vaginal preparations of metronidazole, although clinical data are lacking.', 'Others', 'Pharmacologic response and serum lithium levels should be monitored more closely whenever metronidazole is added to or withdrawn from therapy, and the lithium dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102689/', '', 'Pantoprazole, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Cimetidine, Esomeprazole, Ranitidine, Nizatidine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102571, 'Tipranavir', 'Metronidazole (topical)', 'Moderate', 'Ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, and tipranavir capsules all contain alcohol, which may produce a disulfiram-like reaction when coadministered with drugs that can inhibit aldehyde dehydrogenase (ALDH) such as nitroimidazoles (e.g., metronidazole, tinidazole), nitrofurans (e.g., furazolidone, nifurtimox), and cephalosporins with an N-methylthiotetrazole (NMTT) side chain that structurally resembles disulfiram. Following ingestion of alcohol, inhibition of ALDH results in increased concentrations of acetaldehyde, the accumulation of which can produce an unpleasant physiologic response referred to as the ''disulfiram reaction''. Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion. Severe reactions may result in respiratory depression, cardiovascular collapse, arrhythmia, myocardial infarction, acute congestive heart failure, unconsciousness, convulsions, and death. The interaction is well established for disulfiram.', 'DDInter', 'Until further information is available, use of ritonavir capsules, ritonavir oral solution, lopinavir-ritonavir oral solution, or tipranavir capsules with nitroimidazoles (including vaginal formulations), nitrofurans, and certain cephalosporins should be avoided if possible.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102690/', '', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Famotidine, Cimetidine, Nizatidine, More', 'Paritaprevir, Maraviroc, Simeprevir, Zanamivir, Rilpivirine, Velpatasvir, Etravirine, Acyclovir, Brincidofovir, Darunavir, Doravirine, More', 1767369485);
