INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102751, 'Zoledronic acid', 'Moxetumomab Pasudotox', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', 'DDInter', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102870/', '', 'Tiludronic acid, Alendronic acid, Vosoritide, Denosumab, Cholecalciferol, Burosumab, Risedronic acid, Romosozumab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102752, 'Chromic chloride', 'Acetohexamide', 'Moderate', 'Pharmacologic dosages of chromium (200 mcg/day or more for adults) may help improve diabetic control and reduce the requirements for insulin and other antidiabetic agents. Chromium appears to play a role in normal insulin function and glucose utilization.', 'DDInter', 'Until further data are available, therapy with pharmacologic dosages of chromium should be administered cautiously in patients receiving insulin or other antidiabetic agents. Patients should be monitored for changes in diabetic medication requirements.', 'Synergism', 'Until further data are available, therapy with pharmacologic dosages of chromium should be administered cautiously in patients receiving insulin or other antidiabetic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102871/', '', 'Simvastatin, Guar gum, Rosuvastatin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102753, 'Iron', 'Zinc sulfate', 'Minor', 'Iron and zinc may reduce each other''s absorption when they are coadministered orally. The mechanism of interaction is unknown. In clinical studies, concurrent supplementation with iron and zinc was associated with reduced efficacy in improving the growth and iron and zinc status of malnourished infants compared to supplementation with either one alone. In any case, the clinical significance is probably minimal in healthy individuals. However, monitoring for reduced therapeutic response may be advisable in malnourished patients. Limited data suggest that the magnitude of the interaction may be lessened by separating the times of administration of each by 12 hours.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/102872/', '', 'Iron sucrose, Cyanocobalamin, Folic acid', 'Lidocaine, Diltiazem, Tetracaine, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, Betamethasone, More', 1767369485);
