INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102941, 'Quinidine', 'Nadolol', 'Moderate', 'Quinidine and beta-blockers may have additive negative inotropic effects, which can be beneficial when used to treat atrial fibrillation. However, adverse effects have been reported with quinidine and some beta blockers.', 'DDInter', 'Patients should be monitored for orthostatic hypotension, cardiac arrhythmias, bradycardia, and heart failure, and beta-blocker dosage should be decreased if necessary.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103060/', '', 'Propafenone, Procainamide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102942, 'Racepinephrine', 'Nadolol', 'Major', 'Beta-blockers may antagonize the effects of beta-2 adrenergic bronchodilators and precipitate acute, life-threatening bronchospasm in patients with asthma or other obstructive airway diseases. The mechanism involves increased airway resistance and reduced bronchodilation due to blockade of beta-2 adrenergic receptors. The interaction may also occur with ophthalmically applied beta-blockers, which are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Due to opposing effects on beta-2 adrenergic receptors, propranolol has been used in the treatment of albuterol overdose. Likewise, beta-2 adrenergic agonists may interfere with the pharmacologic effects of beta-blockers.', 'DDInter', 'Concomitant use of beta-2 adrenergic bronchodilators with beta-blockers, including ophthalmic formulations, should generally be avoided. If coadministration is required, a cardioselective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, metoprolol, nebivolol) is usually preferred. Nevertheless, caution is advised and respiratory status should be closely monitored, as cardioselectivity is not absolute and larger doses of beta-1 selective agents may pose some of the same risks as nonselective agents. In general, nonselective beta-blockers are considered contraindicated in patients with obstructive airways disease.', 'Antagonism', 'Concomitant use of beta-2 adrenergic bronchodilators with beta-blockers, including ophthalmic formulations, should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103061/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102943, 'Reserpine', 'Nadolol', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', 'DDInter', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103062/', '', 'Deserpidine, Deserpidine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (102944, 'Ritodrine', 'Nadolol', 'Major', 'The use of a nonselective beta blocker with ritodrine (a beta-2 agonist) may result in mutually antagonistic effects, negating the effect of one or both drugs.', 'DDInter', 'In general, the use of beta blockers with ritodrine is discouraged. Selective beta blockers (e.g. acebutolol, atenolol, betaxolol, bisoprolol, metoprolol) may be preferable if the use of a beta blocker is unavoidable.', 'Antagonism', 'In general, the use of beta blockers with ritodrine is discouraged.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103063/', '', 'Bremelanotide, Black cohosh, Flibanserin', '', 1767369485);
