INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103027, 'Esomeprazole', 'Erlotinib', 'Major', 'Concurrent administration of agents that increase gastric pH such as proton pump inhibitors may decrease the oral bioavailability of erlotinib and reduce its concentrations in plasma. The solubility of erlotinib decreases when the pH is over 5, resulting in reduced absorption. Moreover, since proton pump inhibitors affect pH of the upper gastrointestinal tract for an extended period, separation of doses may not eliminate the interaction.', 'DDInter', 'The concomitant use of erlotinib with proton pump inhibitors should generally be avoided. If acid-suppression therapy is required during erlotinib treatment, an H2-receptor antagonist such as ranitidine may be considered. Available pharmacokinetic data suggest that erlotinib should be administered once a day, at least 2 hours before or 10 hours after dosing of the H2-receptor antagonist. Additionally, the lowest effective dosage of the H2-receptor antagonist should be prescribed.', 'Absorption', 'The concomitant use of erlotinib with proton pump inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103146/', '', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Vemurafenib, Ribociclib, Cobimetinib, Ibrutinib, More', 'Tinidazole, Levofloxacin, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Amoxicillin, Valdecoxib, Fenoprofen, Meclofenamic acid, Ketorolac, Mefenamic acid, More', 1767369485);
