INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103154, 'Regorafenib', 'Naproxen', 'Major', 'Coadministration of regorafenib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with regorafenib is associated with a high frequency of hemorrhage and thrombocytopenia. Coadministration of regorafenib with strong UGT1A9 inhibitors such as mefenamic acid or diflunisal may theoretically increase systemic exposure to regorafenib.', 'DDInter', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with regorafenib.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103273/', '', 'Ritodrine, Bremelanotide, Flibanserin, Cabergoline, Bromocriptine, Tolazoline, Rabeprazole, Dimethyl sulfoxide, Rabeprazole, Misoprostol, Glucosamine, More', 'Entrectinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Larotrectinib, Bosutinib, Selumetinib, Ribociclib, Vandetanib, Cobimetinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103155, 'Repaglinide', 'Naproxen', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', 'DDInter', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral antidiabetic dosage(s) may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their doctor if it occurs. Patients should be observed for loss of glycemic control when these drugs are withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103274/', '', 'Bremelanotide, Flibanserin, Cabergoline, Black cohosh, Bromocriptine, Tolazoline, Dimethyl sulfoxide, Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine', 'Lixisenatide, Albiglutide, Guar gum, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Troglitazone, Ertugliflozin, Semaglutide, Linagliptin, More', 1767369485);
