INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103456, 'Atorvastatin', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of HMG-CoA reductase inhibitors (i.e., statins) that are metabolized by the isoenzyme. Lovastatin and simvastatin are particularly susceptible because of their low oral bioavailability, but others such as atorvastatin and cerivastatin may also be affected.', 'DDInter', 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor. It is advisable to monitor lipid levels and use the lowest effective statin dose. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed. Fluvastatin, pravastatin, and rosuvastatin are not expected to interact with CYP450 3A4 inhibitors.', 'Metabolism', 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103575/', '', 'Ramipril, Valsartan, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103457, 'Avanafil', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of avanafil, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The dosage of avanafil should not exceed 50 mg once every 24 hours when used in combination with moderate CYP450 3A4 inhibitors.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103576/', '', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103458, 'Avapritinib', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9. Increased exposure to avapritinib may increase the risk and/or severity of serious adverse effects such as intracranial hemorrhage, cognitive impairment, mood disorders, hallucinations, edema, and decreases in hemoglobin, leukocytes, neutrophils, and platelets.', 'DDInter', 'Concomitant use of avapritinib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the starting dosage of avapritinib from 300 mg orally once daily to 100 mg orally once daily.', 'Metabolism', 'Concomitant use of avapritinib with moderate CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103577/', '', 'Sunitinib, Futibatinib, Regorafenib, Vemurafenib, Lenvatinib, Dabrafenib, Erdafitinib, Vandetanib, Trametinib, Nintedanib, Ibrutinib, More', '', 1767369485);
