INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103624, 'Netupitant', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', 'DDInter', 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103743/', '', 'Lidocaine, Chloroprocaine, Bupivacaine, Tetracaine, Mepivacaine, Meloxicam, Procaine, Prilocaine, Articaine, Etidocaine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103625, 'Palonosetron', 'Levofloxacin', 'Moderate', 'Certain quinolones, including levofloxacin, norfloxacin, and ofloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Although the risk of a serious interaction is probably low, caution is recommended if levofloxacin, norfloxacin, or ofloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Although the risk of a serious interaction is probably low, caution is recommended if levofloxacin, norfloxacin, or ofloxacin is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103744/', '', 'Nabilone, Scopolamine, Rolapitant, Aprepitant, Dronabinol', 'Enoxacin, Nalidixic acid, Cinoxacin, Trovafloxacin, Delafloxacin, Tinidazole, Tetracycline, Dexlansoprazole, Rabeprazole, Misoprostol, Bismuth subcitrate potassium, More', 1767369485);
