INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103641, 'Netupitant', 'Lurasidone', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lurasidone and its active metabolite, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors. If a moderate CYP450 3A4 inhibitor is added to existing lurasidone therapy, the lurasidone dosage should be reduced by half. Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycaemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, cognitive and motor impairment, seizures, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103760/', '', 'Fluphenazine, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103642, 'Netupitant', 'Lurbinectedin', 'Major', 'Coadministration with a moderate CYP450 3A4 inhibitor is predicted to increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity. Concomitant use of lurbinectedin and moderate CYP450 3A4 inhibitors should generally be avoided. If the coadministration of lurbinectedin with a moderate CYP450 3A4 inhibitor cannot be avoided, a dose reduction for lurbinectedin may be considered.', 'DDInter', 'Close clinical and laboratory monitoring is advised whenever a moderate inhibitor is added to or withdrawn from therapy. Patients should be advised to contact their physician if they experience symptoms suggestive of myelosuppression or hepatotoxicity, such as fever or any other signs of infection, unusual bruising or bleeding; nausea or vomiting, or pain on the right side of the abdomen.', 'Metabolism', 'Close clinical and laboratory monitoring is advised whenever a moderate inhibitor is added to or withdrawn from therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/103761/', '', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', '', 1767369485);
