INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103919, 'Pravastatin', 'Niacin', 'Moderate', 'The concomitant use of pravastatin and niacin may increase the risk of severe myopathy and rhabdomyolysis. Although this reaction has not been reported in clinical trials where pravastatin and niacin were used in combination, the manufacturer warns of an increased risk of myopathy when HMG-CoA reductase inhibitors are used concurrently with niacin.', 'DDInter', 'The concurrent use of niacin and pravastatin should be avoided unless the benefit outweighs risk. Some authorities suggest that a dose reduction for pravastatin should be considered if concomitant therapy is required. It should also be noted that the risk of adverse effects is increased with niacin doses greater than 1 gram per day. In addition, if patients do receive this combination, they should be instructed to promptly report symptoms of muscular pain, weakness, or tenderness. Creatine kinase should be measured if such symptoms appear. If creatine kinase is elevated or if myopathy is suspected, the drugs should be discontinued.', 'Synergism', 'The concurrent use of niacin and pravastatin should be avoided unless the benefit outweighs risk.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104038/', '', 'Fenofibric acid, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Niacin, Bempedoic acid, Colesevelam, More', 'Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Pravastatin, Colesevelam, Alirocumab, Pravastatin, Cyclandelate, Tolazoline, Phenoxybenzamine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103920, 'Remdesivir', 'Niacin', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104039/', '', 'Gemfibrozil, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Ezetimibe, Bempedoic acid, Colesevelam, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 1767369485);
