INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103930, 'Salicylic acid (sodium)', 'Niacin', 'Minor', 'Salicylates may cause an elevation in serum levels of nicotinic acid. The mechanism is not completely understood. However, salicylates also significantly decrease the serum levels of nicotinuric acid--a metabolite--and some experts hypothesize that salicylic acid causes a concentration-dependent decrease in the total clearance of nicotinic acid. Such a decrease would result in the saturation of the nicotinuric acid conjugation pathway. Data are available for aspirin only. No special precautions appear to be necessary.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104049/', '', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, More', '', 1767369485);
