INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (103932, 'Telbivudine', 'Niacin', 'Moderate', 'Prolonged use of certain nucleoside reverse transcriptase inhibitors may commonly cause myopathy, including rare cases of rhabdomyolysis. It is not known if the risk of myopathy is increased during concurrent administration of other agents associated with myopathy, including corticosteroids, fibric acid derivatives, HMG-CoA reductase inhibitors, chloroquine, hydroxychloroquine, penicillamine, and lipid-lowering doses of niacin.', 'DDInter', 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together. Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever.', 'Synergism', 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104051/', '', 'Mipomersen, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Colestipol, Lomitapide, Ezetimibe, Bempedoic acid, Colesevelam, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 1767369485);
