INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104087, 'Heparin', 'Desmopressin', 'Moderate', 'Drugs suspected of causing diabetes insipidus such as demeclocycline, lithium, foscarnet and clozapine may antagonize the antidiuretic effect and may decrease the pressor effect of vasopressin. Alcohol, noradrenaline and heparin also may decrease the antidiuretic effect of vasopressin when given concurrently. The mechanism of this interaction is unclear.', 'DDInter', 'Hemodynamic monitoring is recommended. In addition, vasopressin dosage may be adjusted as needed if co-administered with any of the abovementioned drugs.', 'Antagonism', 'Hemodynamic monitoring is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104206/', '', 'Terlipressin, Oxytocin', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 1767369485);
