INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104164, 'Bexarotene', 'Mestranol', 'Major', 'RECOMMENDED: There are no data concerning the potential effect of bexarotene on hormonal contraceptives. Presumably, coadministration with bexarotene may decrease the efficacy of contraceptives containing low-dose estrogens and progestins due to its induction of CYP450 3A4, the isoenzyme that is primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene. Because use of bexarotene is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment. Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bexarotene.', 'Metabolism', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with bexarotene.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104283/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104165, 'Bosentan', 'Mestranol', 'Major', 'RECOMMENDED: Coadministration with bosentan may decrease the plasma concentrations and efficacy of contraceptive hormones. The mechanism is bosentan induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with bosentan. Because use of bosentan is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment. Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bosentan. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed. Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with bosentan.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104284/', '', 'Tamsulosin, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Trichlormethiazide, Ciclopirox, More', '', 1767369485);
