INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104167, 'Human C1-esterase inhibitor', 'Mestranol', 'Moderate', 'Thromboembolism may occur during treatment with C1 esterase inhibitors, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', 'DDInter', 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators. Close monitoring for thromboembolic events is recommended during and after administration of C1 esterase inhibitors. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104286/', '', 'Voxelotor, Betibeglogene autotemcel, Streptokinase, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104168, 'Carbenicillin', 'Mestranol', 'Moderate', 'RECOMMENDED: The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents. Most antimicrobials, with the exception of the rifamycins and possibly griseofulvin, do not induce hepatic enzymes and have not been shown to significantly increase the clearance of oral contraceptive estrogens. Some investigators believe that antimicrobials interfere with the enterohepatic recirculation of estrogens by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating parent estrogen molecules following first-pass metabolism. It is possible that a small number of women may be more susceptible to contraceptive failure and, consequently, are more sensitive to the effects of antimicrobials on estrogen disposition in vivo, but risk factors or genetic predispositions have yet to be identified.', 'DDInter', 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy. Alternative or additional methods of birth control should be considered during and for at least one week beyond the last dose of short-term antimicrobial therapy, and for at least the initial weeks of long-term antimicrobial therapy when risk may be the greatest.', 'Antagonism', 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104287/', '', 'Nafcillin, Sulbactam, Procaine benzylpenicillin, Tazobactam', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104169, 'Carfilzomib', 'Mestranol', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of hormonal contraception also associated with this adverse effect can potentiate the risk.', 'DDInter', 'Hormonal contraception associated with a risk of thrombosis should generally be avoided during use of carfilzomib. Advise female and male patients of reproductive potential to use effective contraception or abstain from sexual activity during treatment with carfilzomib for at least 30 and 90 days, respectively, following completion of therapy. Advise the patient to contact their physician immediately if pregnancy does occur during these times.', 'Synergism', 'Hormonal contraception associated with a risk of thrombosis should generally be avoided during use of carfilzomib.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104288/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', '', 1767369485);
