INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104344, 'Tipranavir', 'Mestranol', 'Moderate', 'Use of tipranavir in combination with estrogens may be associated with an increased risk of skin rash. The mechanism has not been described. Mild to moderate rashes including urticarial rash, maculopapular rash, and possible photosensitivity have been reported in 14% of females and 8% to 10% of males receiving tipranavir in combination with low-dose ritonavir in phase 2 and 3 trials.', 'DDInter', 'Women who use estrogens should be advised of a potentially increased risk of skin rash during concomitant treatment with tipranavir. If a rash occurs, it is usually mild to moderate. Nevertheless, patients should be instructed to contact their physician promptly, as a temporary discontinuation of tipranavir or the estrogen may be necessary.', 'Synergism', 'Women who use estrogens should be advised of a potentially increased risk of skin rash during concomitant treatment with tipranavir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104463/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', '', 1767369485);
