INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104392, 'Cocaine (nasal)', 'Lisdexamfetamine', 'Major', 'Concomitant use of cocaine with sympathomimetic agents such as epinephrine and phenylephrine may lead to additive cardiovascular effects. The proposed mechanism involves increased circulating catecholamines and increased activity of the sympathetic nervous system.', 'DDInter', 'Concomitant use of cocaine with sympathomimetic agents should be avoided. If coadministration is required, prolonged vital sign and ECG monitoring may be necessary.', 'Synergism', 'Concomitant use of cocaine with sympathomimetic agents should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104511/', '', 'Citicoline, Solriamfetol', '', 1767369485);
