INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104476, 'Zileuton', 'Obeticholic acid', 'Moderate', 'Coadministration with obeticholic acid may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 1A2 activity by obeticholic acid and its glycine and taurine conjugates.', 'DDInter', 'Caution is advised when obeticholic acid is prescribed with drugs that undergo metabolism by CYP450 1A2, particularly those with a narrow therapeutic range such as theophylline or tizanidine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever obeticholic acid is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when obeticholic acid is prescribed with drugs that undergo metabolism by CYP450 1A2, particularly those with a narrow therapeutic range such as theophylline or tizanidine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104595/', '', 'Maralixibat, Ursodeoxycholic acid, Cholic Acid, Odevixibat, Chenodeoxycholic acid', '', 1767369485);
