INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104511, 'Fluoxetine', 'Entrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', 'DDInter', 'Caution is advised when entrectinib is used with CYP450 3A4 inhibitors. Pharmacologic response to entrectinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the entrectinib dosage adjusted as necessary in accordance with the product labeling. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised when entrectinib is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104630/', '', 'Phenelzine, Duloxetine, Tranylcypromine, Vilazodone, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, More', 'Dabrafenib, Pexidartinib, Tepotinib, Larotrectinib, Bosutinib, Cobimetinib, Capmatinib, Afatinib, Duvelisib, Tivozanib, Brigatinib, More', 1767369485);
