INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104661, 'Fluoxetine', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', 'DDInter', 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104780/', '', 'Duloxetine, Vilazodone, Paroxetine, Desvenlafaxine, St. John''s Wort, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, More', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Acetohydroxamic acid, Trospium, Yohimbine, Avanafil, Ammonium chloride, Dimethyl sulfoxide, Mepenzolate, Propantheline, More', 1767369485);
