INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (104703, 'Fluoxetine', 'Propafenone', 'Moderate', 'Coadministration with certain selective serotonin reuptake inhibitors (SSRIs) may increase the plasma concentrations of propafenone. The mechanism is SSRI inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of propafenone in most people.', 'DDInter', 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with propafenone. Lower initial dosages of the antiarrhythmic agent may be appropriate. Patients who are already stabilized on their antiarrhythmic regimen should be monitored for altered effects on myocardial conduction following addition or discontinuation of SSRI therapy (up to 2 weeks for most SSRIs and 5 weeks for fluoxetine due to its long half-life), and the antiarrhythmic dosage adjusted if necessary. Alternatively, an antidepressant that does not interfere with CYP450 2D6 metabolism may be considered, such as citalopram, escitalopram, or venlafaxine.', 'Metabolism', 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with propafenone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/104822/', '', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Vilazodone, Esketamine', 'Tocainide, Moricizine, Bretylium', 1767369485);
